import React from "react";
import MeditationChart from "../Charts/MeditationChart.js"

function MeditationTable({ askToDelete, meditationTable, meditationChart }) {

  if (meditationSessions.length) {
    const table = meditationSessions.map((a) => {
      return (
        <tr key={a.id}>
          <td>{a.date}</td>
          <td>{a.minutes} minutes</td>
          <td>{a.notes}</td>
          <td>
            <button onClick={(e) => askToDelete(e, a)}>Delete activity</button>
          </td>
        </tr>
      );
    });

    return (
      <>
        <table className="records">
          <tbody>
            <td>
              <b>
                <em>Meditation Session</em>
              </b>
            </td>
            <td>Length of activity</td>
            <td>Personal notes</td>
            {table}
          </tbody>
        </table>
        <MeditationChart meditationTable={meditationTable} meditationChart={meditationChart}/>
      </>
    );
  } else {
    return null;
  }
}

export default MeditationTable;
