import { useState } from 'react'
import User from './components/User'
import Home from './components/Home'
// import Login from './components/Login'

export default function App () {
  const [user, setUser] = useState();
  const [activityOptions, setActivityOptions] = useState(initialOptions);
  const [activity, setActivity] = useState([]);

  // if (!user) return <Login onLogin={setUser} />;

  const header = <h1>Leg Up</h1>
  const initialOptions = [
    { value: "yoga", label: "🤸‍♂️ Yoga" },
    { value: "cardio", label: "🏃🏽 Cardio" },
    { value: "meditation", label: "🧘 Meditation" },
    { value: "create_new", label: "✏️ Add your own activity!" },
];

function fetchActivities(e) {
  e.preventDefault()
console.log("In fetch the activities function");

useEffect(() => {
    fetch(`activitiesAPI`)
      .then((response) => {
          if (response.ok) {
          return response.json();
      } else {
        throw new Error ('Bad response');
      }
    })
      .then(setActivityOptions)
      .catch((err) => {
        console.log(`failed to fetch activities`);
      });
  }, []);
}

  // function handleChange(e) {
  //   // console.log(e.value)
  //   setActivity({activity: e.value}, () => activity);
  //   // console.log(activity)
  // }

  return (
    <div className='App'>
      <User header={header} />
      <Home header={header} fetchActivities={fetchActivities}/>
    </div>
  )
}
