import React, { useState } from "react";
import Select from "react-select";
import Meditations from "./Activities/Meditations";
import Yogas from "./Activities/Yogas";
const backend_API = `http://localhost:3000/activities`;

function Home({ currentUser }) {
  const [activityHash, setActivityHash] = useState(null);
  const [activity, setActivity] = useState("");
  const [selected, setSelected] = useState(false);

  console.log('current user:', currentUser)

  const activityOptions = [
    { value: "meditation", label: "ðŸ§˜ Meditation" },
    { value: "yoga", label: "ðŸ¤¸â€â™‚ï¸ Yoga" },
    { value: "cardio", label: "ðŸƒðŸ½ Cardio" },
  ];

  function handleCardioSubmit(e) {
    e.preventDefault();
    setActivityHash([]);

    const user_id = currentUser.id
    const date = e.target.date.value;
    const name = activity;
    const workout = e.target.workout.value;
    const distance = e.target.distance.value;
    const minutes = e.target.minutes.value;
    const notes = e.target.notes.value;

    fetch(`${backend_API}`, {
      method: "POST",
      headers: {
        Accepts: "application/json",
        "Content-Type": "application/json",
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
      body: JSON.stringify({
        user_id,
        name,
        date,
        workout,
        distance,
        minutes,
        notes,
      }),
    }).then(() => {
      fetch(`${backend_API}`, {
        method: "GET",
        headers: {
          Accepts: "application/json",
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      })
        .then((res) => res.json())
        .then((data) => {
          setActivityHash(data);
        });
    });
  }

  function askToDelete(e, meditation) {
    e.preventDefault();

    window.confirm(`Delete ${activity} from database?`);

    if (window.confirm) {
      fetch(`${backend_API}/${meditation.id}`, {
        method: "DELETE",
        headers: {
          Accepts: "application/json",
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      }).then(() => {
        fetch(`${backend_API}`, {
          method: "GET",
          headers: {
            Accepts: "application/json",
            "Content-Type": "application/json",
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        })
          .then((res) => res.json())
          .then((data) => {
            setActivityHash(data);
          });
      });
    }
  }

  if (localStorage.getItem('token')) {
  return (
    <>
      <div className="welcome_blurb">
        <h5>Welcome, {localStorage.getItem("current user")}</h5>
        <h5>
          Down to meditate? Into yoga? Always on the move? Get a Leg-Up on life
          by logging your self-care sessions using the menu below, so you can
          keep track of your self-care practice.
        </h5>
      </div>
      <div className="activity_div">
        <Select
          className="activity_dropdown"
          autofocus
          isSearchable
          placeholder="Select an activity to submit new sessions, and see your progress!"
          options={activityOptions}
          onChange={(e) => {
            fetch(`${backend_API}`, {
              method: "GET",
              headers: {
                Accepts: "application/json",
                "Content-Type": "application/json",
                Authorization: `Bearer ${localStorage.getItem("token")}`,
              },
            })
              .then((res) => res.json())
              .then((data) => {
                console.log(data);
                setActivityHash(data);
                setSelected(true);
                setActivity(e.value);
              });
          }}
        />
        {selected ? (
          <div className="forms">
            {activity === "meditation" ? (
              <Meditations currentUser={currentUser} activity={activity} activityHash={activityHash} setActivityHash={setActivityHash} askToDelete={askToDelete} />
            ) : null}
            {activity === "yoga" ? (
              <Yogas currentUser={currentUser} activity={activity} activityHash={activityHash} setActivityHash={setActivityHash} askToDelete={askToDelete} />
            ) : null}
            {activity === "cardio" ? (
              <>
                <form className="form" onSubmit={handleCardioSubmit}>
                  <label htmlFor="name">Cardio workout:</label>
                  <div className="inputs">
                    <input type="text" name="date" placeholder="MM/DD/YYYY" />
                    <input
                      type="text"
                      name="workout"
                      placeholder="Type (walk/run/hike)"
                    />
                    <input
                      type="number"
                      name="distance"
                      placeholder="Distance (miles)"
                    />
                    <input
                      type="number"
                      name="minutes"
                      placeholder="Time (minutes)"
                    />
                    <input type="text" name="notes" placeholder="Notes?" />
                    <button type="submit">Submit</button>
                  </div>
                </form>
                {cardioSessions.length ? (
                  <CardioChart
                    cardioLabels={cardioLabels}
                    cardioTime={cardioTime}
                  />
                ) : null}
                <CardioTable
                  cardioSessions={cardioSessions}
                  askToDelete={askToDelete}
                />
              </>
            ) : null}
          </div>
        ) : null}
      </div>
    </>
  )
} else {
  return (
    <div id="please_login">
      <h5>Please log in</h5>
    </div>
  );
}
}

export default Home;
