    //       "Content-Type": "application/json",
    //     },
    //     body: JSON.stringify(yogaSession),
    //   };
  }

  function handleCardioSubmit(e) {
    e.preventDefault();
    console.log(e.target);
    // POST request
    // const cardioSession = {
    //   type,
    //   distance,
    //   time,
    //   date,
    // };

    // fetch("/cardio"),
