import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
const API = "http://localhost:3000";

function Login({ onLogin }) {
  const [loginUser, setLoginUser] = useState("");
  const [loginPassword, setLoginPassword] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    console.log(localStorage.getItem("jwt"))
    if (localStorage.getItem("jwt")) {navigate("activity_forms")}
  })

  function handleSubmit(e) {
    e.preventDefault();

    const loginData = {
      user: { username: loginUser, password: loginPassword },
    };

    function onLoggedIn() {
      // console.log('LOGGED IN:', json.jwt);
      // localStorage.setItem('jwt', json.jwt);
      navigate("/activity_forms");
    }

    fetch(`${API}/login`, {
      method: "POST",
      headers: {
        Accepts: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(loginData),
    })
      .then((res) => res.json())
      .then((json) => localStorage.setItem("jwt", json.jwt));

    setLoginUser("");
    setLoginPassword("");
    onLoggedIn();
  }

  return (
    <>
      {!localStorage.getItem("jwt") ? (
        <button
          id="logout"
          onClick={() => {
            localStorage.setItem("jwt", "");
            navigate("/");
          }}
        >
          Logout
        </button>
      ) : (
          <div class="signup_login">
            <form className="signup_login_form" onSubmit={handleSubmit}>
              <div className="username">
                <label htmlFor="username">Username</label>
                <input
                  type="text"
                  id="username"
                  value={loginUser}
                  onChange={(e) => setLoginUser(e.target.value)}
                />
              </div>
              <div className="pass">
                <label htmlFor="password">Password</label>
                <input
                  type="password"
                  id="password"
                  value={loginPassword}
                  onChange={(e) => setLoginPassword(e.target.value)}
                />
              </div>
              <button type="submit">Login</button>
            </form>
              <button id="need_account" onClick={() => navigate("/")}>
                Create new account
              </button>
          </div>
      )}
    </>
  );
}

export default Login;
